import * as React from "react";
import {
  Text,  View,  StyleSheet,  SafeAreaView,  ImageBackground,
  Image,  FlatList,  TouchableOpacity,  ScrollView ,TextInput, Picker,
} from "react-native";
//import DropDownPicker from 'react-native-dropdown-picker';

import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesome5 from "react-native-vector-icons/FontAwesome5";
import colors from "../../assets/colors/colors";

const image = require("../../assets/image/bgyl.png");

function Signup2({navigation}) {
  return (
    <ImageBackground source={image} style={styles.imageBgContainer}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
            <Ionicons
              name="arrow-back-outline"
              size={32}
              color={colors.white}
              style={{ marginTop: 6 }}
            />
          </TouchableOpacity>

        <View style={styles.header}>
            <View style={{flexDirection: 'row', paddingTop: 20}}> 
                <Text style={styles.titleTitle}> Đặt mục tiêu </Text>
            </View>
        </View>

            <Text> 
                Vui lòng đặt mục tiêu theo tháng cho bé
            </Text>
            <Picker style={{borderWidth: 0}}>
                <Picker.Item label="--g/tháng--" value="none" enables="false" />
                <Picker.Item label="4" value="4"/>
                <Picker.Item label="5" value="5"/>
            </Picker>
        <TouchableOpacity style={styles.button} onPress={()=>alert("đăng nhập")}><Text style={styles.textButton}>Nhập</Text></TouchableOpacity>
        
    </ImageBackground>
  )
}

const styles = StyleSheet.create({
    imageBgContainer: {
      flex: 1,
    },
    header:{
        flex: 1,    
      },

    titleTitle: {
        paddingLeft: 30,
        paddingVertical: 10,
        fontSize: 32,
        fontWeight: "bold",
        color: colors.white,
      },
    logo:{
        justifyContent: 'center',
        alignSelf:'center',
        width: 50,
        height: 50,
      },
    footer:{
        flex:1,
        padding: 30,
      },
    ttinfo:{
        paddingTop: 10,
        paddingBottom: 5,
        fontWeight: "400",
        fontSize: 16
      },  
    inputInfo:{
        alignItems:'center',
        flexDirection: "row",
        borderRadius: 10,
        borderColor: colors.red,
        borderWidth: 1.5,
        paddingLeft:10,
        backgroundColor: colors.white
      },
    textInput:{
        padding: 5
      },
      forgot:{
        paddingTop: 10,
        paddingBottom: 5,
        fontWeight: "600",
        fontSize: 13,
        color: colors.white,
      },
      button:{
        marginTop: 20,
        justifyContent:'center',
        alignItems: 'center',
        height: 50,
        borderRadius: 16,
        backgroundColor: colors.red,
        borderColor: colors.red,
        borderWidth: 1.5
      },
      textButton:{
        fontWeight: "700",
        fontSize: 16,
        color: colors.white,
      },
      hasaccount:{
        paddingTop: 10,
        fontWeight: "400",
        fontSize: 13,
        color: colors.black,
        alignSelf: 'center',
      },
      login:{
        fontWeight: "600",
        fontSize: 13,
        color: colors.red,
      }
  });
export default Signup2
