import * as React from "react";
import {
  Text,  View,  StyleSheet,  SafeAreaView,  ImageBackground,
  Image,  FlatList,  TouchableOpacity,  ScrollView
} from "react-native";

import FontAwesome5 from "react-native-vector-icons/FontAwesome5";
import colors from "../../assets/colors/colors";

const image = require("../../assets/image/bgyl.png");

function Signup({navigation}) {
  return (
    <ImageBackground source={image} style={styles.imageBgContainer}>
        <View style={styles.header}>
            <View style={{flexDirection: 'row', paddingTop: 50}}> 
                <Text style={styles.titleTitle}> Meowthy </Text>
                <Image 
                    style={styles.logo} 
                    source={require("../../assets/image/logo.png")}  
                    resizeMode="stretch"
                /> 
            </View>
            <Text style={styles.titleTitle}> Đăng ký </Text>
        </View>

        <View style={styles.footer}>
            <Text style={styles.ttinfo}> Tên đăng nhập </Text>
            
        </View>
    </ImageBackground>
  )
}

const styles = StyleSheet.create({
    imageBgContainer: {
      flex: 1,
    },
    header:{
        flex: 1,    
      },

    titleTitle: {
        paddingLeft: 30,
        paddingVertical: 10,
        fontSize: 32,
        fontWeight: "bold",
        color: colors.white,
      },
    logo:{
        justifyContent: 'center',
        alignSelf:'center',
        width: 50,
        height: 50,
      },
    footer:{
        flex:1,
        padding: 30,
      },
    
  });
export default Signup
