import * as React from "react";
import {
  Text,
  View,
  StyleSheet,
  SafeAreaView,
  ImageBackground,
  TouchableOpacity,
  ScrollView,
} from "react-native";

import Feather from "react-native-vector-icons/Feather";
import colors from "../../assets/colors/colors";
import diaryData from "../../assets/data/diaryData";
const image = require("../../assets/image/bgpurple.png");
import DateTimePicker from "@react-native-community/datetimepicker";

function Diary({ navigation }) {
  const [data, setData] = React.useState(...diaryData);
  const [date, setDate] = React.useState(new Date());
  const [show, setShow] = React.useState(false);
  const [text, setText] = React.useState("HÃ´m nay");

  const onChange = (event, selectedDate) => {
    const currentDate = selectedDate || date;
    setShow(false);
    setDate(currentDate);

    let tempDate = new Date(currentDate);
    let fDate =
      tempDate.getDate() +
      "/" +
      (tempDate.getMonth() + 1) +
      "/" +
      tempDate.getFullYear();
    setText(fDate);
  };

  return (
    <ImageBackground source={image} style={styles.imageBgContainer}>
      <ScrollView
        contentInsetAdjustmentBehavior="automatic"
        showsVerticalScrollIndicator={false}
      >
        <SafeAreaView>
          <View style={styles.titleWrapper}>
            <Text style={styles.titleTitle}>Nháº­t kÃ½</Text>
            <TouchableOpacity
              onPress={() => {
                setShow(true);
              }}
            >
              <Feather
                name="calendar"
                size={32}
                color={colors.white}
                paddingRight={5}
              />
            </TouchableOpacity>
          </View>

          <Text style={styles.date}>{text}</Text>

          {show && (
            <DateTimePicker
              testID="dateTimePicker"
              value={date}
              display="default"
              onChange={onChange}
            />
          )}

          {/* wrapper 1 */}
          <View style={styles.boxWrapper}>
            {/* thá»©c Än */}
            <View style={styles.boxtItem}>
              <View style={styles.boxWrapperLeft}>
                <View style={styles.boxWrapperTop}>
                  <View style={{ flexDirection: "row" }}>
                    <Text style={styles.text_s16_w600}>Thá»©c Än </Text>
                    <Text style={styles.text_s13_w600}>(Kcal)</Text>
                  </View>

                  <Text style={styles.text_s32_w600}>XXX</Text>
                  <Text style={styles.text_s13_w400}>Cáº£ ngÃ y</Text>
                </View>
                <View style={styles.boxWrapperBottom}>
                  <Text style={styles.text_s13_600}>Má»¥c tiÃªu </Text>
                  <Text style={styles.text_s16_w600}>XXX</Text>
                </View>
              </View>
              <View style={styles.boxWrapperRight}>
                <Feather name="chevron-right" size={32} color={colors.black} />
              </View>
            </View>
            {/* cÃ¢n náº·ng */}
            <View style={styles.boxtItem}>
              <View style={styles.boxWrapperLeft}>
                <View style={styles.boxWrapperTop}>
                  <View style={{ flexDirection: "row" }}>
                    <Text style={styles.text_s16_w600}>CÃ¢n náº·ng </Text>
                    <Text style={styles.text_s13_w600}>(Kg)</Text>
                  </View>

                  <Text style={styles.text_s32_w600}>X.XX</Text>
                  <Text style={styles.text_s13_w400}>XX thÃ¡ng XX</Text>
                </View>
                <View style={styles.boxWrapperBottom}>
                  <Text style={styles.text_s13_600}>Má»¥c tiÃªu </Text>
                  <Text style={styles.text_s16_w600}>X.XX</Text>
                </View>
              </View>
              <View style={styles.boxWrapperRight}>
                <Feather name="chevron-right" size={32} color={colors.black} />
              </View>
            </View>
          </View>
          {/* wrapper 2 */}
          <View style={styles.boxWrapperLine2}>
            {/* táº­p luyá»n */}
            <TouchableOpacity onPress={() => navigation.navigate("Exercise")}>
              <View style={styles.boxtItem}>
                <View style={styles.boxWrapperLeftLine2}>
                  <View style={styles.boxWrapperTop}>
                    <Text style={styles.text_s16_w600}>Táº­p luyá»n </Text>
                  </View>
                  <View>
                    <Text style={[styles.text_s13_w400, styles.tapluyenNote]}>
                      {data.exercise}
                    </Text>
                  </View>
                </View>
                <View style={styles.boxWrapperRight}>
                  <Feather
                    name="chevron-right"
                    size={32}
                    color={colors.black}
                  />
                </View>
              </View>
            </TouchableOpacity>
            {/* nÆ°á»c */}
            <View style={styles.boxtItem}>
              <View style={styles.boxWrapperLeft}>
                <Text style={styles.text_s16_w600}>NÆ°á»c</Text>
                <View style={[styles.boxWrapperTop, styles.circle]}>
                  <Text style={[styles.text_s16_w600, styles.center]}>
                    {data.water_amount}%
                  </Text>
                </View>
                <View style={styles.boxWrapperBottom}>
                  <Text style={styles.text_s13_400}>XXX ml</Text>
                  <Text style={styles.text_s13_w400}>YYY ml</Text>
                </View>
              </View>
              <View style={styles.boxWrapperRight}>
                <Feather name="chevron-right" size={32} color={colors.black} />
              </View>
            </View>
          </View>

          {/* vá» bÃ© */}
          <TouchableOpacity onPress={() => navigation.navigate("AboutCat")}>
            <View style={styles.boxtItemVeBe}>
              <View style={styles.boxWrapperLeft}>
                <View style={styles.boxWrapperTop}>
                  <View style={{ flexDirection: "row" }}>
                    <Text style={styles.text_s16_w600}>Vá» bÃ© </Text>
                  </View>

                  <Text style={styles.text_s13_w400}>{data.about}</Text>
                </View>
              </View>
              <View style={styles.boxWrapperRight}>
                <Feather name="chevron-right" size={32} color={colors.black} />
              </View>
            </View>
          </TouchableOpacity>
          <Text style={[styles.text_s13_w600, styles.redTextsuggest]}>
            Tham kháº£o cháº¿ Äá» dinh dÆ°á»¡ng vÃ  bÃ i táº­p cho bÃ©
          </Text>
        </SafeAreaView>
      </ScrollView>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  imageBgContainer: {
    flex: 1,
    padding: 30,
  },

  titleWrapper: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginTop: 30,
  },

  titleTitle: {
    paddingVertical: 10,
    fontSize: 32,
    fontWeight: "bold",
    color: colors.white,
  },
  date: {
    paddingTop: 10,
    fontSize: 16,
    fontWeight: "400",
    color: colors.white,
    marginBottom: 8,
  },

  boxtWhite: {},
  text_s13_w600: {
    fontSize: 13,
    fontWeight: "600",
  },
  text_s13_w400: {
    fontSize: 13,
    fontWeight: "400",
  },
  text_s32_w600: {
    fontSize: 32,
    fontWeight: "600",
  },
  text_s16_w600: { fontSize: 16, fontWeight: "bold" },
  text_s13_w600: { fontSize: 13, fontWeight: "bold" },

  boxWrapper: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  boxWrapperLine2: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 15,
  },
  boxtItem: {
    flexDirection: "row",
    justifyContent: "space-between",
    width: 140,
    height: 140,
    borderRadius: 16,
    borderColor: "#A9A9A9",
    borderWidth: 1,
    backgroundColor: colors.white,
  },
  boxWrapperLeft: {
    flex: 1,
    paddingLeft: 15,
    paddingTop: 10,
    flexDirection: "column",
    justifyContent: "space-between",
  },
  boxWrapperLeftLine2: {
    flex: 1,
    paddingLeft: 15,
    paddingTop: 10,
    flexDirection: "column",
  },

  boxWrapperTop: {},

  boxWrapperBottom: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingBottom: 10,
  },
  boxWrapperRight: {
    alignSelf: "center",
  },
  tapluyenNote: {
    height: 80,
  },
  center: {
    alignSelf: "center",
    alignItems: "center",
    marginVertical: 15,
  },
  circle: {
    flex: 1,
    borderRadius: 100,
    borderWidth: 10,
    borderColor: "#70CCB8",
  },
  boxtItemVeBe: {
    marginTop: 15,
    paddingBottom: 20,
    flexDirection: "row",
    justifyContent: "space-between",
    borderRadius: 16,
    borderColor: "#A9A9A9",
    borderWidth: 1,
    backgroundColor: colors.white,
  },
  redTextsuggest: {
    marginTop: 15,
    color: colors.dark_red,
  },
});

export default Diary;
