import * as React from "react";
import {
  Text,
  View,
  Image,
  StyleSheet,
  SafeAreaView,
  FlatList,
  ScrollView,
  TouchableOpacity,
} from "react-native";

import Feather from "react-native-vector-icons/Feather";
import Ionicons from "react-native-vector-icons/Ionicons";
import { Icon } from "react-native-elements";

import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import categoriesData from "../../assets/data/categoriesData";
import popularData from "../../assets/data/popularData";
import colors from "../../assets/colors/colors";

function Home({ navigation }) {
  const renderCategoryIteam = ({ item }) => {
    return (
      <View
        style={[
          styles.categoryIteamWrapper,
          {
            backgroundColor: item.selected ? colors.yellow : colors.red,
            marginLeft: item.id == 1 ? 20 : 0,
          },
        ]}
      >
        <Image source={item.image} style={styles.categoryIteamImage} />

        <Text style={styles.categoryItemTitle}>{item.title}</Text>

        <View
          style={[
            styles.categoryselectWrapper,
            {
              backgroundColor: item.selected ? colors.red : colors.yellow,
            },
          ]}
        >
          <Feather
            name="chevron-right"
            size={16}
            style={styles.categoryselectIcon}
          />
        </View>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <ScrollView
        contentInsetAdjustmentBehavior="automatic"
        showsVerticalScrollIndicator={false}
      >
        <SafeAreaView>
          <View style={styles.headerWrapper}>
            <Ionicons
              name="arrow-back-outline"
              size={50}
              color={colors.dark_red}
            />
            <Image
              source={require("../../assets/image/solidlogo.png")}
              style={styles.profileImage}
            />
          </View>

          <View style={styles.titleWrapper}>
            <Text style={styles.titlSubitle}>Food</Text>
            <Text style={styles.titleTitle}>Delivery</Text>
          </View>

          <View style={styles.searchWrapper}>
            <Feather name="search" size={20} />
            <View style={styles.search}>
              <Text style={styles.searchText}>search</Text>
            </View>
          </View>

          <View style={styles.categoriesWrapper}>
            <Text style={styles.categoriesText}>Categories</Text>
            <View style={styles.categoriesListWrap}>
              <FlatList
                showsHorizontalScrollIndicator={false}
                data={categoriesData}
                renderItem={renderCategoryIteam}
                keyExtractor={(item) => item.id}
                horizontal={true}
              />
            </View>
          </View>
          {/* popular */}

          <View style={styles.populardWrap}>
            <Text style={styles.categoriesText}>Popular</Text>
            {popularData.map((item) => (
              <TouchableOpacity
                key={item.id}
                onPress={() =>
                  navigation.navigate("Detail", {
                    item: item,
                  })
                }
              >
                <View
                  style={[
                    styles.popularCardWrap,
                    {
                      marginTop: item.id == 1 ? 10 : 20,
                    },
                  ]}
                >
                  <View>
                    <View>
                      <View style={styles.populardTopWrap}>
                        <MaterialCommunityIcons
                          name="crown"
                          size={12}
                          color={colors.dark_red}
                        />
                        <Text style={styles.populardTopText}>
                          Top of the week
                        </Text>
                      </View>

                      <View style={styles.populardtitleWrap}>
                        <Text style={styles.populardTitleText}>
                          {item.title}
                        </Text>
                        <Text style={styles.populardTitleWeight}>
                          Weight: {item.weight}
                        </Text>
                      </View>
                    </View>
                    <View style={styles.popularCardBottom}>
                      <View style={styles.addPzButton}>
                        <Feather
                          name="plus"
                          size={10}
                          color={colors.dark_red}
                        />
                      </View>
                      <View style={styles.ratingWrapper}>
                        <MaterialCommunityIcons
                          name="star"
                          size={10}
                          color={colors.dark_red}
                        />
                        <Text style={styles.rating}>{item.rating}</Text>
                      </View>
                    </View>
                  </View>
                  <View style={styles.popularCardRight}>
                    <Image
                      source={require("../../assets/image/cat.png")}
                      style={styles.popularCardImage}
                    />
                  </View>
                </View>
              </TouchableOpacity>
            ))}
          </View>
        </SafeAreaView>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  headerWrapper: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingTop: 30,
    paddingStart: 20,
    paddingEnd: 20,
  },

  profileImage: {
    width: 50,
    height: 50,
    borderRadius: 40,
  },
  titleWrapper: {
    marginTop: 30,
    paddingStart: 20,
  },
  titleSubtitle: {
    fontSize: 16,
    color: colors.yellow,
  },

  titleTitle: {
    marginTop: 5,
    fontSize: 32,
    color: colors.downy,
  },

  searchWrapper: {
    flexDirection: "row",
    alignItems: "center",
    paddingHorizontal: 20,
    marginTop: 30,
  },

  search: {
    flex: 1,
    marginLeft: 10,
    borderBottomColor: colors.downy,
    borderBottomWidth: 2,
  },

  searchText: {
    fontSize: 14,
    marginBottom: 5,
    color: colors.yellow,
  },

  categoriesWrapper: {
    marginTop: 30,
  },
  categoriesText: {
    fontSize: 16,
    paddingLeft: 20,
    fontWeight: "bold",
  },
  categoriesListWrap: { paddingTop: 15, paddingBottom: 20 },

  categoryIteamWrapper: {
    backgroundColor: colors.yellow,
    marginEnd: 20,
    borderRadius: 10,
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.05,
    shadowRadius: 10,
    elevation: 2,
  },
  categoryIteamImage: {
    width: 60,
    height: 60,
    marginTop: 25,
    marginHorizontal: 20,
    alignSelf: "center",
  },
  categoryItemTitle: {
    fontSize: 14,
    marginTop: 10,
    paddingHorizontal: 20,
    textAlign: "center",
  },
  categoryselectWrapper: {
    alignSelf: "center",
    justifyContent: "center",
    marginTop: 20,
    width: 26,
    height: 26,
    borderRadius: 26,
    marginBottom: 20,
  },
  categoryselectIcon: {
    alignSelf: "center",
  },
  populardWrap: {
    paddingHorizontal: 20,
  },
  popularCardWrap: {
    backgroundColor: colors.purple,
    paddingTop: 20,
    paddingLeft: 20,
    flexDirection: "row",
    borderRadius: 25,
    overflow: "hidden",
    shadowColor: "black",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.05,
    shadowRadius: 10,
    elevation: 2,
  },
  populardTopWrap: {
    flexDirection: "row",
    alignItems: "center",
  },

  populardTopText: {
    marginLeft: 10,
    fontSize: 14,
    fontWeight: "bold",
  },
  populardtitleWrap: { marginTop: 20 },
  populardTitleText: {
    fontSize: 14,
  },
  populardTitleWeight: {
    fontSize: 12,
    fontWeight: "100",
    marginTop: 5,
  },

  popularCardBottom: {
    flexDirection: "row",
    alignItems: "center",
    marginTop: 10,
    marginLeft: -20,
  },
  addPzButton: {
    backgroundColor: colors.yellow,
    paddingVertical: 20,
    paddingHorizontal: 40,
    borderBottomLeftRadius: 25,
    borderTopRightRadius: 25,
  },
  ratingWrapper: {
    flexDirection: "row",
    alignItems: "center",
    marginLeft: 20,
  },
  rating: {
    fontSize: 12,
    marginLeft: 5,
  },

  popularCardRight: {
    marginLeft: 40,
  },
  popularCardImage: {
    resizeMode: "contain",
    width: 210,
    height: 125,
  },
});

export default Home;
