import * as React from "react";
import {
  Text,  View,  StyleSheet,  SafeAreaView,  ImageBackground,
  Image,  TextInput,  TouchableOpacity,  ScrollView, Dimensions,
} from "react-native";

import FontAwesome5 from "react-native-vector-icons/FontAwesome5";
import colors from "../../assets/colors/colors";

const image = require("../../assets/image/bgpurple.png");

function LoginForgot1({navigation}) {
  return (
    <ImageBackground source={image} style={styles.imageBgContainer}>
      <View style={styles.header}>
        <Text style={styles.titleTitle}> Quên mật khẩu </Text>
      </View>
      <View style={styles.footer}>
        <Text style={styles.ttinfo}> Tên đăng nhập </Text>
        <View style={styles.inputInfo}> 
        <FontAwesome5 name="user"  color={colors.black} size={16}/>
        <TextInput
            style={styles.textInput}
            placeholder="Email/SĐT"
          />
        
        </View>
        
        <TouchableOpacity style={styles.button} onPress={()=>navigation.navigate("LoginForgot1")}><Text style={styles.textButton}>Nhập</Text></TouchableOpacity>
        

      </View>
    </ImageBackground>
  );
}
const {height} = Dimensions.get("screen");
const height_logo = height*0.1;
const styles = StyleSheet.create({
  imageBgContainer: {
    flex: 1,
  },
  header:{
    flex: 1,    
  },
  
  titleTitle: {
    paddingTop: 50,
    paddingLeft: 30,
    paddingVertical: 10,
    fontSize: 32,
    fontWeight: "bold",
    color: colors.white,
  },
  logo:{
    justifyContent: 'center',
    alignSelf:'center',
    width: height_logo,
    height: height_logo,
  },
  footer:{
    flex:1,
    padding: 30,
  },
  ttinfo:{
    paddingTop: 10,
    paddingBottom: 5,
    fontWeight: "400",
    fontSize: 16
  },  
  inputInfo:{
    alignItems:'center',
    flexDirection: "row",
    borderRadius: 10,
    borderColor: colors.yellow,
    borderWidth: 1.5,
    paddingLeft:5,
    backgroundColor: colors.white
  },
  textInput:{
    padding: 5
  },
  forgot:{
    paddingTop: 10,
    paddingBottom: 5,
    fontWeight: "600",
    fontSize: 13,
    color: colors.white,
  },
  button:{
    justifyContent:'center',
    alignItems: 'center',
    height: 50,
    borderRadius: 16,
    backgroundColor: colors.yellow,
    borderColor: colors.yellow,
    borderWidth: 1.5
  },
  textButton:{
    fontWeight: "700",
    fontSize: 16,
    color: colors.white,
  },
  noaccount:{
    paddingTop: 10,
    fontWeight: "400",
    fontSize: 13,
    color: colors.black,
  },
  register:{
    fontWeight: "600",
    fontSize: 13,
    color: colors.yellow,
  }
});


export default LoginForgot1
