import * as React from "react";
import {
  Text,  View,  StyleSheet,  SafeAreaView,  ImageBackground,
  Image,  FlatList,  TouchableOpacity,  ScrollView
} from "react-native";

import Feather from "react-native-vector-icons/Feather";
import FontAwesome5 from "react-native-vector-icons/FontAwesome5";
import colors from "../../assets/colors/colors";
import Ionicons from "react-native-vector-icons/Ionicons";
import { Icon } from "react-native-elements";

const image = require("../../assets/image/bgyl.png");

function Signup({navigation}) {
    return (
        <ImageBackground source={image} style={styles.imageBgContainer}>
          <View style={styles.header}>
            <Text style={styles.titleTitle}> Đăng nhập </Text>
            <Image 
            style={styles.logo} 
            source={require("../../assets/image/logo.png")}  
            resizeMode="stretch"
            /> 
          </View>
          <View style={styles.footer}>
            <Text style={styles.ttinfo}> Tên đăng nhập </Text>
            <View style={styles.inputInfo}> 
            <FontAwesome5 name="user"  color={colors.black} size={16}/>
            <TextInput
                style={styles.textInput}
                placeholder="Email/SĐT"
              />
            </View>
            <Text style={styles.ttinfo}> Mật khẩu </Text>
            <View style={styles.inputInfo}> 
            <FontAwesome5 name="lock" color={colors.black} size={16}/>
            <TextInput
                style={styles.textInput}
                placeholder="Mật khẩu"
            />
            </View>
            <TouchableOpacity onPress={() => navigation.navigate("LoginForgot1")} >
              <Text style={styles.forgot}>Quên mật khẩu ?</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={()=>alert("đăng nhập")}><Text style={styles.textButton}>Đăng nhập</Text></TouchableOpacity>
            <Text style={styles.noaccount}>Chưa có tài khoản ? <TouchableOpacity onPress={()=> navigation.navigate("Signup")}>
              <Text style={styles.register}>Đăng ký</Text>
            </TouchableOpacity> </Text>
    
          </View>
        </ImageBackground>
      );
    }
    const {height} = Dimensions.get("screen");
    const height_logo = height*0.1;
    const styles = StyleSheet.create({
      imageBgContainer: {
        flex: 1,
      },
      header:{
        flex: 1,    
      },
      
      titleTitle: {
        paddingTop: 50,
        paddingLeft: 30,
        paddingVertical: 10,
        fontSize: 32,
        fontWeight: "bold",
        color: colors.white,
      },
      logo:{
        justifyContent: 'center',
        alignSelf:'center',
        width: height_logo,
        height: height_logo,
      },
      footer:{
        flex:1,
        padding: 30,
      },
      ttinfo:{
        paddingTop: 10,
        paddingBottom: 5,
        fontWeight: "400",
        fontSize: 16
      },  
      inputInfo:{
        alignItems:'center',
        flexDirection: "row",
        borderRadius: 10,
        borderColor: colors.yellow,
        borderWidth: 1.5,
        paddingLeft:5,
        backgroundColor: colors.white
      },
      textInput:{
        padding: 5
      },
      forgot:{
        paddingTop: 10,
        paddingBottom: 5,
        fontWeight: "600",
        fontSize: 13,
        color: colors.white,
      },
      button:{
        justifyContent:'center',
        alignItems: 'center',
        height: 50,
        borderRadius: 16,
        backgroundColor: colors.yellow,
        borderColor: colors.yellow,
        borderWidth: 1.5
      },
      textButton:{
        fontWeight: "700",
        fontSize: 16,
        color: colors.white,
      },
      noaccount:{
        paddingTop: 10,
        fontWeight: "400",
        fontSize: 13,
        color: colors.black,
      },
      register:{
        fontWeight: "600",
        fontSize: 13,
        color: colors.yellow,
      }
    });
    
export default Signup
