import * as React from "react";
import {
  Text,  View,  StyleSheet,  SafeAreaView,  ImageBackground,
  Image,  TextInput,  TouchableOpacity,  ScrollView, Dimensions,
} from "react-native";

import FontAwesome5 from "react-native-vector-icons/FontAwesome5";
import colors from "../../assets/colors/colors";

const image = require("../../assets/image/bgpurple.png");

function Login({navigation}) {
  return (
    <ImageBackground source={image} style={styles.imageBgContainer}>
      <View style={styles.header}>
        <Text> Đăng nhập </Text>
        <Image 
        style={styles.logo} 
        source={require("../../assets/image/logo.png")}  
        resizeMode="stretch"
        /> 
      </View>
      <View style={styles.footer}>
        <Text style={styles.ttinfo}> Tên đăng nhập </Text>
        <View style={styles.inputInfo}> 
        <FontAwesome5 name="user-0" color="#1111" size={20}/>
        <TextInput
            style={styles.textInput}
            placeholder="Email/SĐT"
          />
        </View>
        <Text style={styles.ttinfo}> Mật khẩu </Text>
        <View style={styles.inputInfo}> 
        <TextInput
            style={styles.inputText}
            placeholder="Mật khẩu"
        />
        </View>
        <TouchableOpacity onPress={()=>alert("quên")}><Text>Quên mật khẩu ?</Text></TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={()=>alert("quên")}><Text>Đăng nhập</Text></TouchableOpacity>
        <Text>Chưa có tài khoản ? <TouchableOpacity onPress={()=>alert("đăng ký")}><Text>Đăng ký</Text></TouchableOpacity> </Text>

      </View>
    </ImageBackground>
  );
}
const {height} = Dimensions.get("screen");
const height_logo = height*0.25;
const styles = StyleSheet.create({
  imageBgContainer: {
    flex: 1,
  },
  header:{
    flex: 1,
    justifyContent: 'center',
    alignItems:'center',
  },
  logo:{
    width: height_logo,
    height: height_logo,
  },
  footer:{
    flex:1,
    padding: 30,
  },
  inputInfo:{
    flexDirection: "row",
    borderRadius: 10,
    borderColor: colors.yellow,
    borderWidth: 1.5
  },
  button:{
    justifyContent:'center',
    alignItems: 'center',
    height: 50,
    borderRadius: 16,
    backgroundColor: colors.yellow,
    borderColor: colors.yellow,
    borderWidth: 1.5
  }
});


export default Login
